package evennumbers;

public class EvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int x=0;x<=99;x++)
			
			if((x%2)==0){
			
			System.out.println(x+1);
		
			
			}
		    else{
		    	
		    	System.out.println(x+1 + " Is an even #");
		    }
		
	}

}
